/* assets/js/libs/particles.min.js
   → particles.js 로드 & 기본 설정 샘플 */
(function(w,d){
  var s=d.createElement('script');
  s.src='https://cdn.jsdelivr.net/npm/particles.js@2.0.0/particles.min.js'; /* [10] */
  s.onload=function(){
    /* DOM에 id="particles-js"가 있으면 기본 입자 배경 적용 */
    if (d.getElementById('particles-js')) {
      w.particlesJS('particles-js', {
        particles:{ number:{ value:35, density:{ enable:true, value_area:800 } },
          color:{ value:'#ffffff' }, line_linked:{ enable:true, color:'#ffffff' },
          move:{ speed:2 } }, retina_detect:true
      });
    }
  };
  d.head.appendChild(s);
})(window,document);
