/* assets/js/libs/slick.min.js
   → Slick Carousel(슬라이더) 동적 삽입 + 기본 테마 */
(function(){
  /* CSS */
  ['slick','slick-theme'].forEach(function(name){
    var l=document.createElement('link');
    l.rel='stylesheet';
    l.href='https://cdn.jsdelivr.net/npm/slick-carousel@1.8.1/slick/'+name+'.css'; /* [3] */
    document.head.appendChild(l);
  });

  /* JS */
  var s=document.createElement('script');
  s.src='https://cdn.jsdelivr.net/npm/slick-carousel@1.8.1/slick/slick.min.js';
  document.head.appendChild(s);
})();
