/* assets/js/libs/wow.min.js
   → Animate.css + WOW.js 초기화 */
(function(){
  /* Animate.css CDN */
  var ac=document.createElement('link');
  ac.rel='stylesheet';
  ac.href='https://cdnjs.cloudflare.com/ajax/libs/animate.css/4.1.1/animate.min.css';
  document.head.appendChild(ac);

  /* WOW.js CDN */
  var s=document.createElement('script');
  s.src='https://cdnjs.cloudflare.com/ajax/libs/wow/1.1.2/wow.min.js'; /* [2] */
  s.onload=function(){ new WOW().init(); };
  document.head.appendChild(s);
})();
